var custOpts;
var custSteps;
var custSecs;
var minLocked;
var maxLocked;

var MT          = Math;
var MATH_ROUND  = MT.round;
var MATH_FLOOR  = MT.floor;
var MATH_CEIL   = MT.ceil;
var MATH_ABS    = MT.abs;


function MATH_LOG(n) {return MT.log(MATH_ABS(n)) / MT.LN10;}
function MATH_POW(n) {return MT.pow(10, n);}
